(9, 1, '2022-08-06 08:08:45', '2022-08-06 08:08:45', '<!-- wp:group {\"align\":\"full\",\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|background\"}}},\"spacing\":{\"padding\":{\"top\":\"0px\",\"bottom\":\"0px\"}}},\"backgroundColor\":\"foreground\",\"textColor\":\"background\",\"layout\":{\"inherit\":true}} -->\n<div class=\"wp-block-group alignfull has-background-color has-foreground-background-color has-text-color has-background has-link-color\" style=\"padding-top:0px;padding-bottom:0px\"><!-- wp:template-part {\"slug\":\"header\",\"theme\":\"twentytwentytwo\",\"tagName\":\"header\",\"align\":\"wide\"} /-->\n\n<!-- wp:gallery {\"linkTo\":\"none\",\"sizeSlug\":\"full\",\"align\":\"wide\"} -->\n<figure class=\"wp-block-gallery alignwide has-nested-images columns-default is-cropped\"><!-- wp:image {\"align\":\"wide\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image alignwide size-full is-style-default\"><img src=\"http://www.zulianob.tk/wordpress/wp-content/themes/twentytwentytwo/assets/images/flight-path-on-transparent-d.png\" alt=\"Ilustración de un pájaro volando.\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery --></div>\n<!-- /wp:group -->\n\n<!-- wp:spacer {\"height\":\"66px\"} -->\n<div style=\"height:66px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->', 'Cabecera (oscura, pequeña)', '', 'publish', 'closed', 'closed', '', 'header-small-dark', '', '', '2022-08-06 08:08:45', '2022-08-06 08:08:45', '', 0, 'http://www.zulianob.tk/wordpress/?p=9', 0, 'wp_template_part', '', 0),
